import React from "react";

// Implementing the Stop Button to allow stopping the current focus or break session
function StopButton({
  setIsTimerRunning,
  setSession,
  setFocusDuration,
  setBreakDuration,
  setDisableDurationButtons,
  setDisableStopButton,
  setActiveSessionStyle,
  setPaused,
  disableStopButton,
}) {
  const handleStopButtonClick = () => {
    setIsTimerRunning(false);
    setSession({
      label: null,
      duration: null,
      timeRemaining: null,
      percentComplete: null,
    });
    setFocusDuration(25);
    setBreakDuration(5);
    setDisableDurationButtons(false);
    // Disabling the stop button when there is no active session
    setDisableStopButton(true);
    setActiveSessionStyle("none");
    setPaused("none");
  };

  return (
    <button
      type="button"
      className="btn btn-secondary"
      data-testid="stop"
      title="Stop the session"
      onClick={handleStopButtonClick}
      disabled={disableStopButton}
    >
      <span className="oi oi-media-stop" />
    </button>
  );
}

export default StopButton;
