import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import {minutesToDuration, secondsToDuration} from "../utils/duration";

import DurationButtons from "./DurationButtons";
import PlayPauseButtons from "./PlayPauseButtons";

// These functions are defined outside of the component to insure they do not have access to state
// and are, therefore more likely to be pure.

/**
 * Update the session state with new state after each tick of the interval.
 * @param prevState
 *  the previous session state
 * @returns
 *  new session state with timing information updated.
 */
function nextTick(prevState, focusDuration, breakDuration) {
  const timeRemaining = Math.max(0, prevState.timeRemaining - 1);
  if (prevState.label === "Focusing") {
    const timeElapsed = (focusDuration * 60) - timeRemaining;
    prevState.percentComplete = (timeElapsed / (focusDuration * 60)) * 100;
  } else {
    const timeElapsed = (breakDuration * 60) - timeRemaining;
    prevState.percentComplete = (timeElapsed / (breakDuration * 60)) * 100;
  }
  return {
    ...prevState,
    timeRemaining,
  };
}

/**
 * Higher order function that returns a function to update the session state with the next session type upon timeout.
 * @param focusDuration
 *    the current focus duration
 * @param breakDuration
 *    the current break duration
 * @returns
 *  function to update the session state.
 */
function nextSession(focusDuration, breakDuration) {
  /**
   * State function to transition the current session type to the next session. e.g. On Break -> Focusing or Focusing -> On Break
   */
  return (currentSession) => {
    if (currentSession.label === "Focusing") {
      return {
        label: "On Break",
        duration: breakDuration,
        timeRemaining: breakDuration * 60,
        percentComplete: 0,
      };
    }
    return {
      label: "Focusing",
      duration: focusDuration,
      timeRemaining: focusDuration * 60,
      percentComplete: 0,
    };
  };
}

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  // The current session - null where there is no session running
  const [session, setSession] = useState({ label: null, duration: null, timeRemaining: null, percentComplete: null });

// ToDo: Allow the user to adjust the focus and break duration.
  // The current Focus Duration timer starts at 25
  const [focusDuration, setFocusDuration] = useState(25);
  // The current Break Duration timer starts at 5
  const [breakDuration, setBreakDuration] = useState(5);
  // Disable state for focusDuration buttons
  const [disableDurationButtons, setDisableDurationButtons] = useState(false);

  // The disabled state of the stop button
  const [disableStopButton, setDisableStopButton] = useState(true);

  const [activeSessionStyle, setActiveSessionStyle] = useState("none");

  // PAUSED text appears after pressing the pause button
  const [paused, setPaused] = useState("none");

  // // Function to handle decreasing the focus or break duration time
  // const handleMinusFocusDurationClick = () => {
  //   setFocusDuration((currentFocusDuration) => {
  //     return Math.max(5, (currentFocusDuration - 5 ));
  //   });
  // }

  // const handleMinusBreakDurationClick = () => {
  //   setBreakDuration((currentBreakDuration) => {
  //     return Math.max(1, (currentBreakDuration - 1 ));
  //   });
  // }

  // // Function to handle increasing the focus or break duration time
  // const handlePlusFocusDurationClick = () => {
  //   setFocusDuration((currentFocusDuration) => {
  //     return Math.min(60, (currentFocusDuration + 5 ));
  //   });
  // }

  // const handlePlusBreakDurationClick = () => {
  //   setBreakDuration((currentBreakDuration) => {
  //     return Math.min(15, (currentBreakDuration + 1 ));
  //   });
  // }

  const handleStopButtonClick = () => {
    setIsTimerRunning(false);
    setSession({
      label: null,
      duration: null, 
      timeRemaining: null,
      percentComplete: null, 
    });
    setFocusDuration(25);
    setBreakDuration(5);
    setDisableDurationButtons(false);
    setDisableStopButton(true);
    setActiveSessionStyle("none");
    setPaused("none");
  }


  /**
   * Custom hook that invokes the callback function every second
   *
   * NOTE: You will not need to make changes to the callback function
   */
  useInterval(() => {
      if (session.timeRemaining === 0) {
        new Audio("https://bigsoundbank.com/UPLOAD/mp3/1482.mp3").play();
        return setSession(nextSession(focusDuration, breakDuration));
      }
      return setSession(nextTick(session, focusDuration, breakDuration));
    },
    // REMEMBER TO CHANGE THIS BACK TO 1000
    isTimerRunning ? 100 : null
  );

  /**
   * Called whenever the play/pause button is clicked.
   */

  function playPause() {
    // Disable FocusDuration buttons upon pressing play
    setDisableDurationButtons(true);
    // Enable the stop button upon pressing play
    setDisableStopButton(false);
    // Display active session div upon pressing play
    setActiveSessionStyle("block");

    setIsTimerRunning((prevState) => {
      // displaying the PAUSED text when the pause button has been pressed
      if (prevState) {
        setPaused("block");
      } else {
        setPaused("none");
      }

      const nextState = !prevState;
      if (nextState) {
        setSession((prevStateSession) => {
          // If the timer is starting and the previous session is null,
          // start a focusing session.
          if (prevStateSession.label === null) {
            return {
              label: "Focusing",
              duration: focusDuration,
              timeRemaining: focusDuration * 60,
              percentComplete: 0,
            };
          }
          return prevStateSession;
        });
      }
      return nextState;
    });
  }

  return (
    <div className="pomodoro">
      <DurationButtons
        focusDuration={focusDuration} 
        setFocusDuration={setFocusDuration}
        breakDuration={breakDuration} 
        setBreakDuration={setBreakDuration}
        disableDurationButtons={disableDurationButtons} 
      />
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <PlayPauseButtons
              playPause={playPause}
              isTimerRunning={isTimerRunning}
            />
            {/* TODO: Implement stopping the current focus or break session. */}
            {/* TODO: Disable the stop button when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="stop"
              title="Stop the session"
              onClick={handleStopButtonClick}
              disabled={disableStopButton}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
            <h2 data-testid="session-title" style={{display: activeSessionStyle}}>
              {session.label} for {minutesToDuration(session.duration)} minutes
            </h2>
            {/* TODO: Update message below correctly format the time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title" style={{display: activeSessionStyle}}>
              {secondsToDuration(session.timeRemaining)} remaining
            </p>
            <h2 style={{display: paused}}>
              PAUSED
            </h2>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px", display: activeSessionStyle }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={session.percentComplete} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${session.percentComplete}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
